import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      mosnters: [
        {
          name: 'Frankenstein',
          id: 1
        },
        {
          name: 'Dracula',
          id: 2
        },
        {
          name: 'Zombie',

        }
      ]
    };
  }
  render() {
    return (
      <div className='App'>
       {
         this.state.mosnters.map(monster => (
           <h1>{monster.name}</h1>
         ))
       }
      </div>
    );
  }
}

export default App;
